{% set total = { 'so_qty': 0, 'dc_qty':0, 'remaining_qty': 0, 'panel_qty':0, 'pkg_qty': 0 } %}

{% for p in range(3) %}
<div class="row">
    <div class="col-md-5 col-xs-5" style="padding:0px; margin:0px;">
        <h1 style="margin:0px; padding:0px;">Delivery Challan {% if doc.status == "Draft" -%}<span style="text-transform:uppercase;">{{ doc.status }}</span>{%- endif %}</h1>
    </div>
    <div class="col-md-3 col-xs-3" style="padding:0px; margin:0px;">
        
    </div>
    <div class="col-md-4 col-xs-4" style="padding:0px; margin:0px;">
        <img style="height:auto; "src="files/dp_logo_300x72.PNG">
    </div>   
</div>

<div style="clear:both; height:20px;"></div>
<div class="row">
    <div class="col-md-5 col-xs-5" style="padding:0px; margin:0px;">
        <b>Delivery To</b>
        <p>{{ doc.customer_name }}</p>
        <p>{% if doc.customer_code != None -%} # {{ doc.customer_code }} {%- endif %}</p>
        <p>{% if doc.contact_display != None -%} {{ doc.contact_display }} {%- endif %}</p>
        <br>
        <b>Fabric Details</b>
        {% if doc.items[0].fabric_gsm != None -%}
        <br><b>Fabric GSM: </b>{{ doc.items[0].fabric_gsm }}
        {%- endif %}
        {% if doc.items[0].fabric_width != None -%}
        <br><b>Fabric Width: </b>{{ doc.items[0].fabric_width }}
        {%- endif %}
        {% if doc.items[0].fabric_material != None -%}
        <br><b>Material: </b>{{ doc.items[0].fabric_material }}
        {%- endif %}
        {% if doc.items[0].fabric_type != None -%}
        <br><b>Fabric Type: </b>{{ doc.items[0].fabric_type }}
        {%- endif %}
        {% if doc.items[0].fabric_construction != None -%}
        <br><b>Fabric Construction: </b>{{ doc.items[0].fabric_construction }}
        {%- endif %}
    </div>
    <div class="col-md-3 col-xs-3" style="padding:0px; margin:0px;">
        <b>
            {% if p == 0 -%}
            Customers Copy
            {% elif p == 1 -%}
                Accounts Copy
            {% elif p == 2 -%}
                Operations Copy
            {%- endif %}
        </b>
        <br>
        <br>
        <b>DC #</b>
        <p> {{ doc.name }} </p>
        <b>Delivery Date</b>
        <br>
        <p>{{ doc.get_formatted('posting_date') }}</p>
    </div>
    <div class="col-md-4 col-xs-4" style="padding:0px; margin:0px;">
        <b>Shipper</b>
        <br>
        <p>Syntech Fibres (Pvt.) Ltd.</p>
        <p>5-3-1 Sector 15,</p>
        <p>Korangi Industrial Area</p>
        <p>Karachi 74900</p>
        <p>hello@duraprints.pk</p>
        <p>duraprints.pk</p>
    </div>   
</div>

<div style="clear:both; height:20px;"></div>
<div class="row">
    <table class="table" style="padding:0px; margin:0px;">
        <thead>
            <tr>
                <th style="width:15px;">Sr</th>
                <th style="width:100p;x">Thumbnail</th>
                <th>Item Description</th>
                <th style="width:30px;">UOM</th>
                <th class="text-right" style="width:60px;">SO<br>Qty.</th>
                <th class="text-right" style="width:60px;">DC<br>Qty.</th>
                <th class="text-right" style="width:60px;">Remaining<br>Qty.</th>
                <th style="width:60px;">PKG<br>No.</th>
                <th class="text-right" style="width:50px;">PKG<br>Qty</btr></th>
                <th class="text-right" style="width:60px;">Unprinted<br>Fabric</th>
            </tr>
        </thead>
        <tbody>
           
            {%- for item in doc.items -%}
            {% set customer_po_number = frappe.db.get_value("Sales Order", {'name': item.against_sales_order }, "po_no" ) %}
            <tr class="text-right">
                <td class="text-center"> {{ loop.index }}</td>
                <td>
                    
                    
                        {% if item.image != '' -%}
                        <img class="image" src="{{ item.image }}" style="max-height: 50px; max-width: 100px; display: block; margin: 0px auto;">
                        {%- endif %} 
                    
                </td>
                <td class="text-left">
                    <div class="pull-left" style="width:270px;">
                        <b>{{ item.item_code }}</b>
                        {% if item.design_name != None -%}
                        <br><b>Design Name: </b>{{ item.design_name }}
                        {%- endif %}
                        {% if item.design_height != None -%}
                        <br><b>Design Height: </b>{{ item.design_height }}
                        {%- endif %}
                        {% if item.design_gap != None -%}
                        <br><b>Design Gap: </b>{{ item.design_gap }}
                        {%- endif %} 
                        {% if item.against_sales_order != None -%}
                        <br><b>SO #: </b>{{ item.against_sales_order }}
                        {%- endif %}
                        {% if customer_po_number != None -%}
                        <br><b>Customer PO #: </b>{{ customer_po_number }}
                        {%- endif %}
                        
                    </div>
                </td>
                {% set lineitem = {'so_qty': frappe.db.get_value("Sales Order Item", {'parent': item.against_sales_order,'item_name': item.item_name }, "qty" ) | float , 'delivered_qty': frappe.db.get_value("Sales Order Item", {'parent': item.against_sales_order,'item_name': item.item_name }, "delivered_qty" ), 'remaining_qty':0, 'panel':0 } %}
                <td>
                    Panel
                    <br>
                    <b>{{ item.uom }}</b>
                    {% if item.uom != item.stock_uom -%}
                    <br>
                    {{ item.stock_uom }}
                    {%- endif %}
                    
                </td>
                <td> 
                    {% if item.design_height != None -%}
                        {{ '{:0,.0f}'.format( ((lineitem.so_qty)/((item.design_height | float + item.design_gap | float)*0.0254)) ) }}
                    {%- endif %}
                    <br>
                {{ '{:2,.2f}'.format(lineitem.so_qty) }} 
                    {% if total.update({'so_qty': total.so_qty + lineitem.so_qty }) %} {% endif %}
                </td>
                <td>
                    {% if item.is_raw_fabric != 1 -%}
                        {% if item.design_height != None -%}
                            {% if lineitem.update({'panel': ((item.qty)/((item.design_height | float + item.design_gap | float)*0.0254))  }) %} {% endif %}
                            {{ '{:0,.0f}'.format( lineitem.panel  ) }}
                            {% if total.update({'panel_qty': total.panel_qty + lineitem.panel }) %} {% endif %}
                            
                        {%- endif %}
                        <br>
                        
                        <b>{{ '{:2,.2f}'.format(item.qty) }}</b>
                        {% if item.is_raw_fabric != 1 -%}
                            {% if total.update({'dc_qty': total.dc_qty + item.qty }) %} {% endif %}
                        {%- endif %}
                    {%- endif %}
                    
                    {% if item.uom != item.stock_uom -%}
                    <br>
                    <b style="font-style: italic;">
                    {{ '{:2,.2f}'.format(item.stock_qty) }}
                    </b>
                    {%- endif %}
                    
                    
                </td>
                
                <td> 
                    {% if item.design_height != None -%}
                        {{ '{:0,.0f}'.format( ((lineitem.so_qty | float - lineitem.delivered_qty | float)/((item.design_height | float + item.design_gap | float)*0.0254)) ) }}
                    {%- endif %}
                    <br>
                    {% if lineitem.update({'remaining_qty': lineitem.so_qty | float - lineitem.delivered_qty | float }) %} {% endif %}
                    
                    {% if total.update({'remaining_qty': total.remaining_qty + lineitem.remaining_qty }) %} {% endif %}
                    
                    {{ '{:2,.2f}'.format(lineitem.remaining_qty) }} 
                    
                </td>
                <td class="text-left">
                    {% if item.package_no != None -%}
                        {{ item.package_no }}
                    {%- endif %}
                </td>
                <td class="text-right">
                    {% if item.package_quantity != None -%}
                        {{ item.package_quantity }}
                        {% if total.update({'pkg_qty': total.pkg_qty + item.package_quantity }) %} {% endif %}
                    {%- endif %}
                </td>
                
                <td class="">
                    {% if item.unprinted_fabric != None -%}
                    <br>
                        {{ '{:2,.2f}'.format(item.unprinted_fabric) }}
                    {%- endif %}
                </td>
            </tr>
            {%- endfor -%}
            <tr class="bold text-right">
                <td class="text-left" colspan="3">Printed Fabric Total</td>
                <td>Panel<br>Meter</td>
                <td class="text-right"></td>
                <td class="text-right">
                    {{ '{:0,.0f}'.format(total.panel_qty) }}
                    <br>
                    {{ '{:2,.2f}'.format(total.dc_qty) }}</td>
                <td class="text-right">
                    <br>
                    {{ '{:2,.2f}'.format(total.remaining_qty) }}
                    </td>
                <td></td>
                <td class="text-right">{{ '{:2,.0f}'.format(total.pkg_qty) }}</td>
                <td></td>
            </tr>
        </tbody>
    </table>
</div>
<div style="clear:both; height:10px;"></div>
<div class="row">
    <div class="col-md-12 col-xs-12 text-left" style="padding:0px;">
        <p>*Remaing quantity is the difference between the Sales Order Quantity and total deliverd quantity.</p>
    </div>
</div>
<div style="clear:both; height:50px;"></div>
<div class="row">
    <div class="col-md-12 col-xs-12 text-left" style="padding:0px;">
        <table class="stamp" style="width:100%; text-align:center;">
            <tr>
                <td style="height:80px; width:25%;">
                    {{ frappe.db.get_value("User", {'email': doc.modified_by }, "full_name" ) }}
                </td>
                <td style="height:80px; width:25%;"></td>
                <td style="height:80px; width:25%;"></td>
                <td style="height:80px; width:25%;"></td>
            </tr>
            <tr>
                <td>Prepared By</td>
                <td>Checked By</td>
                <td>Received By</td>
                <td>Date</td>
            </tr>
        </table>
    </div>
</div>
<div style="clear:both; height:0px;"></div>

    {% if p != 3 -%}
    <div class="page-break"></div>
    {%- endif %}
{%- endfor -%}

