                    {%- endif %}
                    <br>
                {{ '{:2,.2f}'.format(lineitem.so_qty) }} 
                    {% if total.update({'so_qty': total.so_qty + lineitem.so_qty }) %} {% endif %}
                </td>
                <td>
                    {% if item.is_raw_fabric != 1 -%}
                        {% if item.design_height != None -%}
                            {% if lineitem.update({'panel': ((item.qty)/((item.design_height | float + item.design_gap | float)*0.0254))  }) %} {% endif %}
                            {{ '{:0,.0f}'.format( lineitem.panel  ) }}
                            {% if total.update({'panel_qty': total.panel_qty + lineitem.panel }) %} {% endif %}
                            
                        {%- endif %}
                        <br>
                        
                        <b>{{ '{:2,.2f}'.format(item.qty) }}</b>
                        {% if item.is_raw_fabric != 1 -%}
                            {% if total.update({'dc_qty': total.dc_qty + item.qty }) %} {% endif %}
                        {%- endif %}
                    {%- endif %}
                    
                    {% if item.uom != item.stock_uom -%}
                    <br>
                    <b style="font-style: italic;">
                    {{ '{:2,.2f}'.format(item.stock_qty) }}
                    </b>
                    {%- endif %}
                    
                    
                </td>
                
                <td> 
                    {% if item.design_height != None -%}
                        {{ '{:0,.0f}'.format( ((lineitem.so_qty | float - lineitem.delivered_qty | float)/((item.design_height | float + item.design_gap | float)*0.0254)) ) }}
                    {%- endif %}
                    <br>
                    {% if lineitem.update({'remaining_qty': lineitem.so_qty | float - lineitem.delivered_qty | float }) %} {% endif %}
                    
                    {% if total.update({'remaining_qty': total.remaining_qty + lineitem.remaining_qty }) %} {% endif %}
                    
                    {{ '{:2,.2f}'.format(lineitem.remaining_qty) }} 
                    
                </td>
                <td class="text-left">
                    {% if item.package_no != None -%}
                        {{ item.package_no }}
                    {%- endif %}
                </td>
                <td class="text-right">
                    {% if item.package_quantity != None -%}
                        {{ item.package_quantity }}
                        {% if total.update({'pkg_qty': total.pkg_qty + item.package_quantity }) %} {% endif %}
                    {%- endif %}
                </td>
                
                <td class="">
                    {% if item.unprinted_fabric != None -%}
                    <br>
                        {{ '{:2,.2f}'.format(item.unprinted_fabric) }}
                    {%- endif %}
                </td>
            </tr>
            {%- endfor -%}
            <tr class="bold text-right">
                <td class="text-left" colspan="2">Printed Fabric Total</td>
